import React, { Component } from "react";
import { connect } from "react-redux";
import sectionInfo from "../redux/actions/index";
import { graphql } from 'react-apollo'
import { useQuery, gql } from '@apollo/client';


const SECTION_DETAILS = gql `
query AllSections {
  sections {
    name
    text
  }
}
`

function sectionDetails() {
  const {loading, error, data} = useQuery(SECTION_DETAILS);

  if(loading) return <p>Loading</p>
  if (error) return <p>Error :(</p>;

    return data.sections.map(({ name, text }) => (
      <div key={name}>
        <p>
          {name}: {text}
        </p>
      </div>
    ));
}


class MainImage extends Component {
  imageRef: React.RefObject<HTMLImageElement>;
  textChange: React.RefObject<HTMLParagraphElement>;
  constructor(props) {
    super(props);
    this.handleScroll = this.handleScroll.bind(this);
    this.imageRef = React.createRef();
    this.textChange = React.createRef();

    this.state = {
      scrollThreshold: 10,
    };
  }

  // on DOM render  display default image
  componentDidMount = () => {
    window.addEventListener("scroll", this.handleScroll);
    this.imageRef.current.src = "img/01-hero-lightpass/0000.jpg";
    this.props.updateSectionInfo({
      sections: [
        {
          name: "01-hero-lightpass",
          style: "white",
          text:
            "Active Noise Cancellation for immersive sound. Transparency mode for hearing what is happening around you.",
        },
        { name: "02-head-bob-turn", text: "Arrival of the fittest." },
        { name: "03-flip-for-guts", text: "Sound that cuts out the noise." },
        {
          name: "04-explode-tips",
          text:
            "Choose from three sizes of soft, flexible silicone tips that click into place. Find the best fit and get the best sound by using the Ear Tip Fit Test.",
        },
        {
          name: "05-flip-for-nc",
          style: "white",
          text:
            "Enjoy superior sound quality with Adaptive EQ, which automatically tunes music to the shape of your ear for a rich, consistent listening experience.",
        },
        { name: "06-transparency-head" },
        { name: "07-flip-reveal-guts", style: "white" },
        { name: "08-turn-for-chip" },
        { name: "09-scoop-turn", style: "black" },
        {
          name: "10-fall-into-case",
          style: "white",
          text: "Charge wirelessly. Use tirelessly.",
        },
      ],
    });
  };

  componentWillUnmount = () => {
    window.removeEventListener("scroll", this.handleScroll);
  };

  //to create file path to be retrieved from Public folder
  getImagePath(section, index) {
    const i = index / 10;
    let fileName = "";

    if (i >= 1) {
      fileName = index > 99 ? `0${index}` : `00${index}`;
    } else {
      fileName = `000${index}`;
    }
    return `/img/${section}/${fileName}.jpg`;
  }

  handleScroll = (e) => {
    const scrollThreshold = this.state.scrollThreshold;
    let newIndex;

    let sTop = window.scrollY; //number of pixels document is currently scrolled vertically
    let index = sTop / scrollThreshold;

    index = Math.floor(index);

    if (index < 148) {
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[0].name,
        index
      );
      this.textChange.current.innerHTML = this.props.sections[0].text; //text for each section
    } else if (index >= 148 && index < 280) {
      newIndex = index - 148;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[1].name,
        newIndex
      );
      this.textChange.current.innerHTML = this.props.sections[1].text;
    } else if (index >= 280 && index < 368) {
      newIndex = index - 280;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[2].name,
        newIndex
      );
      this.textChange.current.innerHTML = this.props.sections[2].text;
    } else if (index >= 368 && index < 506) {
      newIndex = index - 368;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[3].name,
        newIndex
      );
      this.textChange.current.src = this.props.sections[3].text;
    } else if (index >= 506 && index < 645) {
      newIndex = index - 506;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[4].name,
        newIndex
      );
      this.textChange.current.src = this.props.sections[4].text;
    } else if (index >= 645 && index < 821) {
      newIndex = index - 645;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[5].name,
        newIndex
      );
    } else if (index >= 821 && index < 889) {
      newIndex = index - 821;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[6].name,
        newIndex
      );
    } else if (index >= 889 && index < 978) {
      newIndex = index - 889;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[7].name,
        newIndex
      );
      this.textChange.current.innerHTML = ""; //this section has no text
    } else if (index >= 978 && index < 1212) {
      newIndex = index - 978;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[8].name,
        newIndex
      );
    } else {
      newIndex = index - 1212;
      this.imageRef.current.src = this.getImagePath(
        this.props.sections[9].name,
        newIndex
      );
      this.textChange.current.innerHTML = this.props.sections[9].text;
    }
  };

  render() {
    return (
      <section>
        <div>
          <div className="div-content">
            <p ref={this.textChange}></p>
          </div>
          <img className="responsive" ref={this.imageRef} src=""></img>
        </div>
      </section>
    );
  }
}

//select data from store that connected component needs
const mapStateToProps = (state) => ({
  sections: state.sectionState.sections,
});

const mapDispatchToProps = (dispatch) => ({
  updateSectionInfo: (data) => dispatch(sectionInfo(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(MainImage);
