import {gql}  from "apollo-boost"
import  {graphql} from "react-apollo"
import { Component } from "react";

const SECTION_DETAILS = gql`
  query AllSections {
    sections {
      name
      text
    }
  }
`;

class SectionDetails extends Component {

  displaySections() {
    var data = this.props.data;

    if(data.loading) {
      return (
        <div>
          loading books...
        </div>
      )
    } else {
      data.sections.map(section => {
      <li>{section.name}</li>
      })
    }
  }
  render() {
    console.log("These are",this.props)
    return(
      <div>

    </div>
    )
  }

}

export default graphql(SECTION_DETAILS) (SectionDetails);
