import { gql } from "apollo-boost";
import { graphql, useQuery, Query } from "react-apollo";
import { Component } from "react";
import queryFetch from  "./QueryFetch"

const SECTION_DETAILS = gql`
{
    sections {
      name
      text
  }
}
`;

const SectionDetails = () => {
  const {loading, error, data} = useQuery(SECTION_DETAILS)
  queryFetch()

  if(loading) return <div>Loading...</div>
  if(error) return <div>On ho</div>

  return (
    <div>
      {data.sections.map(section =><div key={section.name}>{section.name}<div>{section.text}</div></div>)}

    </div>
  )
}
export default SectionDetails;
