  //       return (
  //         <li key={section.id}>{section.name}</li>
  //       )

  //     });
  //   }
  // }
  render() {
    console.log("These are", this.props);
    return (
      <div>
        <ul id="sections-list">
          {this.displaySections()}
          </ul>
      </div>
    );
  }
}

export default graphql(SECTION_DETAILS)(SectionDetails);
