import { ApolloServer, gql } from "apollo-server";
const typeDefs = gql`
type Section {
    name: String
    text: String
}

type Query  {
    sections: [Section]
}
`;
const sections = [{
  name: "01-hero-lightpass",
  text: "Section-01"
}, {
  name: "02-bob-turn",
  text: "Section-02"
}];
const resolvers = {
  Query: {
    sections: () => sections
  }
};
const server = new ApolloServer({
  typeDefs,
  resolvers
});
