import React from "react";
import { useMutation } from "@apollo/react-hooks";
import { gql } from "apollo-boost";

const SET_SECTION_DETAILS = gql`
  mutation UpdateSection($name: String!, $text: String!) {
    updateSection(name: $name, text: $text) {
      name
      text
    }
  }
`;

const Footer = () => {
  const [updateSection] = useMutation(SET_SECTION_DETAILS);

  const updateBookDetails = () => {
    updateSection({
      variables: { name: "11-test-section-update", author: "Test successfull" },
    });
  };

  return (
    <footer>
      <div className="footer-main">
        <div className="footer-legal-copyright">
          Copyright Â© 2020 Apple Inc. All rights reserved.
        </div>
        <button onClick={updateBookDetails}> Update Sections</button>
      </div>
    </footer>
  );
};
export default Footer;
