queryFetch(`
query {
    sections {
        name
        text
    }
}`).then(data => {
    data.data.sections.array.forEach(section => {
        const option = document.createElement("option")
        option.value = section.name
    });
})
const queryFetch = (query) => {
    return (
        fetch("url", {
            method: "GET",
            headers: {"Content-type": "application/json"} ,
            body: JSON.stringify ({
                query: query
            })
        }).then(res => res.json)
    )
}
