import React from "react";

const SET_SECTION_DETAILS = gql`
  mutation UpdateBook($name: String!, $author: String!) {
    updateBook(name: $name, author: $author) {
      name
      author
    }
  }
`;

const Footer = () => {
  const [updateBook] = useMutation(SET_SECTION_DETAILS);
  return (
    <footer>
      <div className="footer-main">
        <div className="footer-legal-copyright">
          Copyright Â© 2020 Apple Inc. All rights reserved.
        </div>
        <button> Test Mutation</button>
      </div>
    </footer>
  );
};
export default Footer;
