import { gql } from "apollo-boost";
import { graphql, useQuery } from "react-apollo";
import { Component } from "react";

const SECTION_DETAILS = gql`
{
    sections {
      name
      text
  }
}
`;

interface PropsType {
  sections: object[]

}

class SectionDetails extends Component {


  displaySections() {
      var data = this.props.sections.data;

      if(data.loading){
          return( <div>Loading sections</div> );
      } 
      else {
          return data.sections.map(section => {
              return( <li key={section.id}>{ section.name }</li> );
          });
      }
  }


  render() {
    console.log("These are", this.props);
    return (
      <div>
        <ul id="sections-list">
          {this.displaySections()}
          </ul>
      </div>
    );
  }
}

export default graphql(SECTION_DETAILS)(SectionDetails);
